import React from 'react'
import Lottie from 'react-lottie'
import logo from '../assets/logo.svg';
import Waves from '../assets/waves.json'

function Content() {

  const WavesAnimation = {
    loop:true,
    autoplay:true,
    animationData:Waves,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice"
    }
  };

  return (
    <div className="hero min-h-screen mt-10 rounded-xl bg-base-300">
      <div className="hero-content text-center">
        <div className="items-center max-w-2xl z-40 absolute">
          <div className='items-center'>
            <div className='w-56 h-24 absolute z-10'>
              <Lottie options={WavesAnimation}/>
            </div>
          </div>
          <img src={logo}  alt='logo' className='w-40 h-40 z-50 m-auto'/>
          <h1 className="text-3xl font-bold">Hi, I'm Feby Febri Yansyah</h1>
          <p className="py-6">I’m from Indonesia and I have been working as a UI UX designer for more than 2 years. I’ve worked for a NTC ( Network Tecnology Consultan ) as a Mobile designer and Mobile Developer for 1 years and currently I Freelancer In Upwork and right now I am looking forward to collaborate with you!</p>
          <button className="btn btn-primary">Get Started</button>
        </div>
      </div>
    </div>
  )
}

export default Content
